import React, { useEffect } from "react";
import Boy from "./Boy";

const Content = (props) => {
    //useEffect 的用法有三種 
    //
    //useEffect(() => {componentDidMount + componentDidUpdate內容},[props參數])
    //useEffect(() => {componentDidMount return (ComponentWillUnmount內容);},[])
    //useEffect(() => {componentDidMount + componentDidUpdate內容 return (ComponentWillUnmount內容);},[props參數])
    //
    useEffect(() => {
        if (props.first) {
            document.getElementById("content").style.fontSize = "30px";
        }
        else {
            document.getElementById("content").style.fontSize = "24px";
        }
    }, [props.first]);
    if (props.first) {
        return (
            <>
                <div id="content">
                    <Boy />
                </div>
            </>

        );
    }
    else {
        return (
            <>
                <div id="content">
                </div>
            </>

        );
    }
}

export default Content;
