import React from "react";
import { useEffect } from "react";


const Boy = (props) => {
    useEffect(() => {
        document.getElementById("msg").style.width = ((props.width) + "px");
        document.getElementById("content").style.width = ((props.width) + "px");
    });
    return (
        <>
            <div id="msg">{props.name}</div>
        </>
    );
}

export default Boy;
