                    ÂØÜÁ¢º : <input type="password" name="password" id="password" onChange={handleChange3} /><br />
                    Á¢∫Ë™çÂØÜÁ¢º : <input type="password" name="check-password" id="check-password" onChange={handleChange4} />
                    <button onClick={() => {
                        const regex = /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
                        if(!Data.email || regex.test(Data.email) === false){
                            alert("emailÈåØË™§");
                            return;
                        }
                        if(Data.password !== Data.check_password)
                        {
                            alert("ÂØÜÁ¢ºËàáÁ¢∫Ë™çÂØÜÁ¢º‰∏çÁõ∏Á¨¶");
                            return;
                        }
                        if(Data.password.length < 6)
                        {
                            alert("ÂØÜÁ¢ºÈï∑Â∫¶Ëá≥Â∞ëÈúÄË¶Å6ÂÄãÂ≠óÂÖÉ");
                            return;
                            
                        }
                        var url = 'https://www.axisflow.biz/practice_project/php_server/register.php';
                        let obj = {
                            email: Data.email,
                            username: Data.username,
                            password: Data.password,
                        }
                        fetch(url, {
                            method: 'POST',
                            body: JSON.stringify(obj)
                        }
                        )
                            .then(function (response) {
                                if(response.status === 201)
                                    window.location.href = "https://https://www.axisflow.biz/App/practice_project/Login";
