import React, {Component} from 'react';

class App extends Component {
  constructor(props){
    super(props);
    this.name="名稱1";
  }

  render(){
    return(
      <React.StrictMode>
        <header>
          header
        </header>
          <div id="text-area">
            <button onClick={this.props.handleClick}>
            this.props.name
            </button>
          </div>      
        <footer>
          footer
        </footer>
      </React.StrictMode>
    );
  }
}
