import React from "react";
import fetch from 'node-fetch';



//function component
const Progress = () => {
    const handle_remove = (numberofclass, number, text) => {
        for (let i = 0; i < number.length; i++) {
            if (i === 0)
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = "";
            else
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = "";
        }
        //console.log("remove-" + text);
    }
    const handle_add = (idx) => {
        var tr_child = document.getElementById("trall-" + idx).children;

        /*         
                for(let i = 0 ; i < tr_child.length ; i++)
                {
                    console.log(i + " " + tr_child[i].innerText);
                } 
                */
        /*        
         0 選修
         1 1907
         2 資訊視覺設計
           Information Visualization Design
         3 　
         4 半
         5 2
         6 2
         7 　
         8 278
         9 　
         10 A205
         11 
         12 鄭怡玲
         13 　
         14 文學院
         15 31
         16 　
         17 31
         18 中文
         19 文學院學生優先
         20 新增課程 
         */
        //1 2 8 10 12
        let numberofclass = tr_child[5].innerText;
        let number = numberofclass.substring(1, numberofclass.length);
        console.log(number);
        for (let i = 0; i < number.length; i++) {
            
            if(number[i] === 'A' || number[i] === 'B' || number[i] === 'C' || number[i] === 'D'){
                if(number[i] === 'A')
                {
                    number[i] = 10;
                }
                else if (number[i] === 'B')
                {
                    number[i] = 11;
                }
                else if (number[i] === 'C')
                {
                    number[i] = 12;
                }
                else if (number[i] === 'D')
                {
                    number[i] = 13;
                }
            }
            console.log(parseInt(number[i] - 1));
            if (document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML !== "") {
                alert("不能有衝堂的課!");
                return;
            }
        }
        for (let i = 0; i < number.length; i++) {
            if (i === 0)
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = "<button className='remove' id='remove-" + tr_child[2].innerText + "'>X</button><br/>" + tr_child[3].innerText + "<br/>(<a href='" + tr_child[2].children[0].href + "'>" + tr_child[2].innerText + "</a>)<br/>" + tr_child[8].innerText + "<br/>" + tr_child[7].innerText;
            else
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = tr_child[3].innerText + "<br/>(<a href='" + tr_child[2].children[0].href + "'>" + tr_child[2].innerText + "</a>)<br/>" + tr_child[8].innerText + "<br/>" + tr_child[7].innerText;
        }
        document.getElementById("remove-" + tr_child[2].innerText).addEventListener("click", function () { handle_remove(numberofclass, number, tr_child[2].innerText); }, false);

        //2  3  4  5  6  7  8
        //10 11 12 13 14 15 16
        //                 104
    }
    //useState(傳入參數) 解構給 name 變數 和 changeName 函式 
    return (
        <React.StrictMode>
            <div>
                <button className="submit" onClick={() => {
                    let text;
                    text = "p_check=1&p_subject=" +
                        document.getElementById("sel-subject").value + "&p_year=" +
                        document.getElementById("sel-year").value + "&p_group=" +
                        document.getElementById("sel-group").value + "&p_lang=" +
                        document.getElementById("sel-lang").value + "&p_crsName=" +
                        document.getElementById("sel-crsName").value + "&p_teacher=" +
                        document.getElementById("sel-teacher").value + "&p_week=" +
                        document.getElementById("sel-week").value + "&p_mtg=" +
                        document.getElementById("sel-mtg").value + "&p_emi=" +
                        document.getElementById("sel-emi").value;
                    //console.log(text);
                    //console.log("v_year=&v_career=U&v_excel=0&v_dept=U64F&v_level=&v_lang=&v_text=&v_teach=&v_week=&v_mtg=&v_emi=");
                    fetch('http://localhost:3001/post4', {
                        method: 'POST',
                        body: text,
                        headers: {
                            'Accept': 'text/document',
                            'Content-Type': 'text/plain'
                        }
                    }).then(res => res.text())
                        .then(ress => {
                            var parser = new DOMParser();
                            var doc = parser.parseFromString(ress, 'text/html');
                            //console.log(doc);
                            let myNodeList = doc.getElementsByClassName("word_13");
                            document.getElementById("content").innerHTML = "";
                            for (let i = 0; i < myNodeList.length; i++) {
                                if (myNodeList[i].className === "word_13" && myNodeList[i].tagName === "TABLE") {

                                    document.getElementById("content").innerHTML += "<table class='word_13'  cellspacing='0' cellpadding='0' name='mytable'>" + myNodeList[i].innerHTML + "</table>";
                                }
                            }
                            let line = document.getElementsByTagName("tr")
                            for (let i = 8; i < line.length; i++) {
                                if (line[i].id !== "")
                                    break;
                                line[i].setAttribute("id", "trall-" + i);
                                line[i].innerHTML += "<td><button class='new-add' id='button-" + i + "'>新增課程</button></td>";
                                document.getElementById("button-" + i).addEventListener("click", function () { handle_add(i); }, false)
                            }
                        })

                }}>查詢</button>


            </div>
        </React.StrictMode>
    );
    //fetch(url).then(參數 => 回傳值).then(參數 => 函式內容).catch(err => 回報錯誤)
}

export default Progress;
