import React from "react";
import fetch from 'node-fetch';



//function component
const Progress = () => {
    const handle_remove = (numberofclass,number,text) => {
        for(let i = 0 ; i < number.length ; i++)
        {
            if(i === 0)
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = "";
            else
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = "";
        }
        //console.log("remove-" + text);
    }
    const handle_add = (idx) => {
        var tr_child = document.getElementById("trall-" + idx).children;

/*         
        for(let i = 0 ; i < tr_child.length ; i++)
        {
            console.log(i + " " + tr_child[i].innerText);
        } 
        */
/*        
 0 選修
 1 1907
 2 資訊視覺設計
   Information Visualization Design
 3 　
 4 半
 5 2
 6 2
 7 　
 8 278
 9 　
 10 A205
 11 
 12 鄭怡玲
 13 　
 14 文學院
 15 31
 16 　
 17 31
 18 中文
 19 文學院學生優先
 20 新增課程 
 */
        //1 2 8 10 12
        let numberofclass =  tr_child[8].innerText;
        let number = numberofclass.substring(1,numberofclass.length);
        for(let i = 0 ; i < number.length ; i++)
        {
            if(document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML !== "")
            {
                alert("不能有衝堂的課!");
                return;
            }   
        }
        for(let i = 0 ; i < number.length ; i++)
        {
            if(i === 0)
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = "<button className='remove' id='remove-" + tr_child[1].innerText + "'>X</button><br/>" + tr_child[2].innerText + "<br/>(<a href='" + tr_child[1].children[0].href + "'>" + tr_child[1].innerText +"</a>)<br/>" + tr_child[12].innerText + "<br/>" + tr_child[10].innerText;
            else
                document.getElementById("thead-td-" + (8 * (parseInt(number[i] - 1)) + parseInt(numberofclass[0]) + 1)).innerHTML = tr_child[2].innerText + "<br/>(<a href='" + tr_child[1].children[0].href + "'>" + tr_child[1].innerText +"</a>)<br/>" + tr_child[12].innerText + "<br/>" + tr_child[10].innerText;
        }
        document.getElementById("remove-" + tr_child[1].innerText).addEventListener("click", function () { handle_remove(numberofclass,number,tr_child[1].innerText); }  , false);
        
        //2  3  4  5  6  7  8
        //10 11 12 13 14 15 16
        //                 104
    } 
    //useState(傳入參數) 解構給 name 變數 和 changeName 函式 
    return (
        <React.StrictMode>
            <div>
                <button onClick={() => {
                    let text;
                    if(document.getElementById("sel-year").value === "11121008752333")
                    {
                        text = "v_year=" + document.getElementById("sel-year").value + "&v_career=" +
                        document.getElementById("sel-career").value + "&v_excel=0&v_dept=" +
                        document.getElementById("sel-dept").value + "&v_level=" +
                        document.getElementById("sel-level").value + "&v_lang=" +
                        document.getElementById("sel-lang").value + "&v_text=" +
                        document.getElementById("sel-text").value + "&v_teach=" + 
                        document.getElementById("sel-teach").value + "&v_week=" +
                        document.getElementById("sel-week").value + "&v_mtg=" +
                        document.getElementById("sel-mtg").value + "&v_emi=" +
                        document.getElementById("sel-emi").value;
                        //console.log(text);
                    //console.log("v_year=&v_career=U&v_excel=0&v_dept=U64F&v_level=&v_lang=&v_text=&v_teach=&v_week=&v_mtg=&v_emi=");
                    fetch('http://localhost:3000/', {
                        method: 'POST',
                        body: text,
                        headers: {
                            'Accept': 'text/document',
                            'Content-Type': 'text/plain'
                        }
                    }).then(res => res.text())
                        .then(ress => {
                            var parser = new DOMParser();
                            var doc = parser.parseFromString(ress, 'text/html');
                            //console.log(doc);
                            let myNodeList = doc.getElementsByClassName("word_13");
                            console.log(myNodeList);
                            document.getElementById("content").innerHTML = "";
                            
                            for (let i = 0 ; i < myNodeList.length ; i++) {
                                console.log(myNodeList[i]);
                                if(myNodeList[i].className === "word_13" && myNodeList[i].tagName === "TABLE")
                                {
                                    
                                    document.getElementById("content").innerHTML += "<table class='word_13'  cellspacing='0' cellpadding='0' name='mytable'>"+ myNodeList[i].innerHTML + "</table>";
                                }
                            }
                            let line = document.getElementsByTagName("tr")
                            console.log(document.getElementsByTagName("tr"));
                            for(let i = 9 ; i < line.length ; i++)
                            {
                                if(line[i].id !== "")
                                    break;
                                line[i].setAttribute("id","trall-" + i);
                                line[i].innerHTML += "<td><button className='new-add' id='button-" + i +"'>新增課程</button></td>";
                                document.getElementById("button-" + i).addEventListener("click", function() { handle_add(i); }  , false)
                            }
                        })
                    }
                    else
                    {
                        text = "v_year=" + document.getElementById("sel-year").value + "&v_career=" +
                        document.getElementById("sel-career").value + "&v_dept=" +
                        document.getElementById("sel-dept").value + "&v_level=" +
                        document.getElementById("sel-level").value + "&v_lang=" +
                        document.getElementById("sel-lang").value + "&v_text=" +
                        document.getElementById("sel-text").value + "&v_teach=" + 
                        document.getElementById("sel-teach").value + "&v_week=" +
                        document.getElementById("sel-week").value + "&v_mtg=" +
                        document.getElementById("sel-mtg").value + "&v_emi=" +
                        document.getElementById("sel-emi").value;
                        //console.log(text);
                    //console.log("v_year=&v_career=U&v_excel=0&v_dept=U64F&v_level=&v_lang=&v_text=&v_teach=&v_week=&v_mtg=&v_emi=");
                    fetch('http://localhost:3000/post', {
                        method: 'POST',
                        body: text,
                        headers: {
                            'Accept': 'text/document',
                            'Content-Type': 'text/plain'
                        }
                    }).then(res => res.text())
                        .then(ress => {
                            var parser = new DOMParser();
                            var doc = parser.parseFromString(ress, 'text/html');
                            //console.log(doc);
                            let myNodeList = doc.getElementsByClassName("word_13");
                            console.log(myNodeList);
                            document.getElementById("content").innerHTML = "";
                            
                            for (let i = 0 ; i < myNodeList.length ; i++) {
                                console.log(myNodeList[i]);
                                if(myNodeList[i].className === "word_13" && myNodeList[i].tagName === "TABLE")
                                {
                                    
                                    document.getElementById("content").innerHTML += "<table class='word_13'  cellspacing='0' cellpadding='0' name='mytable'>"+ myNodeList[i].innerHTML + "</table>";
                                }
                            }
                            let line = document.getElementsByTagName("tr");
                            console.log(document.getElementsByTagName("tr"));
                            for(let i = 9 ; i < line.length ; i++)
                            {
                                if(line[i].id !== "")
                                    break;
                                line[i].setAttribute("id","trall-" + i);
                                line[i].innerHTML += "<td><button className='new-add' id='button-" + i +"'>新增課程</button></td>";
                                document.getElementById("button-" + i).addEventListener("click", function() { handle_add(i); }  , false)
                            }
                        })
                    }
                    
                }}>查詢</button>

            </div>
        </React.StrictMode>
    );
    //fetch(url).then(參數 => 回傳值).then(參數 => 函式內容).catch(err => 回報錯誤)
}

export default Progress;
