import React from 'react';
import { NavbarProvider } from './NavbarContext';
import Navbar from './Navbar';

//Navbar
const navlink = {
    Target1: {
        link: "/Login",
        item: "登入"
    },
    Target2: {
        link: "/Register",
        item: "註冊"
    }
};

//Navbar
const navlink2 = {
    Target1: {
        link: "/",
        item: "系級課程"
    },
    Target2: {
        link: "/Activity",
        item: "通識"
    },
    Target3: {
        link: "/Product",
        item: "體育/全校課程"
    },
    Target4: {
        link: "/Teach",
        item: "教育學程"
    },
};

const Header = () => {
    let token = localStorage.getItem("token");
    var url = 'http://localhost/dashboard/practice_project/php_server/auto_login.php';
    let obj = {
        key: token,
    }
    let ret;
    function get(){
        if (token) {
             fetch(url, {
                method: 'POST',
                body: JSON.stringify(obj)
            })
                .then(function (response) {
                    return response.json();
                })
                .then(function (body) {
                    ret = body;
                });

            console.log(ret);
            if (ret.code === 1) {
                return 0;
            }
            else {
                return 1;
            }

        }
        else {
            return 1;
        }
    }
    let v = get();
    console.log(v);
    let ob = (
        <>
            <div id="header">
                <NavbarProvider value={navlink}>
                    <Navbar></Navbar>
                </NavbarProvider>
            </div>
        </>
    );
    let ob2 = (
        <>
            <div id="header">
                <NavbarProvider value={navlink2}>
                    <Navbar></Navbar>
                </NavbarProvider>
            </div>
        </>
    );
    if (v === 1) {
        return (
            <>
                <div id="header">
                    <NavbarProvider value={navlink}>
                        <Navbar></Navbar>
                    </NavbarProvider>
                </div>
            </>
        );
    }
    else {
        return (
            <>
                <div id="header">
                    <NavbarProvider value={navlink2}>
                        <Navbar></Navbar>
                    </NavbarProvider>
                </div>
            </>
        );
    }
}

export default Header;
