import React from "react";


//function component
const Progress = () => {
    //useState(傳入參數) 解構給 name 變數 和 changeName 函式 
    return (
        <React.StrictMode>
            <header>
                header
            </header>
            <div>
                <button onClick={() => {fetch("https://lms2020.nchu.edu.tw/sysdata/attach/bulletin.49354/658b9647142cbc704ba7269e99bd22c1.png")
                .then(function(response) {
                    if(response.ok)
                    {
                        return response.blob();
                    }
                    throw new Error("Network response was not ok");
                }).then(blob => {
                    const img = new Image();
                    // 允許跨域，確保可以正確用於 canvas
                    img.crossOrigin = 'anonymous';
                    
                    // 將blob 轉為dataURL
                    img.src = URL.createObjectURL(blob);
                    
                    // 將元素渲染至 body
                    document.body.appendChild(img);
                })
                }}>按我</button>

            </div>
            <footer>
                footer
            </footer>
        </React.StrictMode>
    );
    //fetch(url).then(參數 => 回傳值).then(參數 => 函式內容).catch(err => 回報錯誤)
}

export default Progress;
