import React from "react";
import fetch from 'node-fetch';


//function component
const Progress = () => {
    //useState(傳入參數) 解構給 name 變數 和 changeName 函式 
    return (
        <React.StrictMode>
            <header>
                header
            </header>
            <div>
                <button onClick={() => {
                    let formData = new FormData();
                    formData.append("v_year", "");
                    formData.append("v_career", "U");
                    formData.append("v_excel", "0");
                    formData.append("v_dept", "U64F");
                    formData.append("v_level", "");
                    formData.append("v_lang", "");
                    formData.append("v_text", "");
                    formData.append("v_teach", "");
                    formData.append("v_week", "");
                    formData.append("v_mtg", "");
                    formData.append("v_emi", "");
                    fetch("http://localhost:8080", {
                        method: "GET",
                    }).then((response) => {
                        response.headers()
                        response.text()})
                    .then((jsonData) => {
                        console.log(jsonData);
                        document.getElementById("content").innerHTML =  jsonData;
                    }).catch((err) => {
                        console.log('錯誤:', err);
                    })
                }}>按我</button>

            </div>
            <footer>
                footer
            </footer>
        </React.StrictMode>
    );
    //fetch(url).then(參數 => 回傳值).then(參數 => 函式內容).catch(err => 回報錯誤)
}

export default Progress;
