import React, {Component} from 'react';
import Boy from './boy';
import NameContext from './nameContext.js'

class App extends Component {
  static contextType = NameContext;
  constructor(props){
    //繼承props
    super(props);
    //宣告state
    this.state = {
      name: "",
      first: true
    }
    //綁定changeName函式的this 為 class App 的 this
    this.changeName = this.changeName.bind(this);

  }
  //changeName 給button onClick用的函式
  changeName = (newName) =>{
    this.setState({name:this.context.name});
  }

  //DerivedStateFromProps 用props來修改state的render phase
  static getDerivedStateFromProps(props,state){
    if(state.first === true)
      return {name:"新的2",first: false};
    else
      return {name: state.name,first: true};
  }
  //componentDidMonut 用來修改render後的DOM樣式 // 可以fetch style DOM...
  componentDidMount(){
    if(this.state.first === true){
      document.getElementById('text-area').style.backgroundColor = "gray";
    }
    else {
      document.getElementById('text-area').style.backgroundColor = "green";
    }
  }
  //componentWillUnmonut   時會觸發 delete
  componentWillUnmount() {
    return;
  }
  //shouldComponentUpdate(nextProps,nextState) 
  //return true 代表可以做 Update 的 render 
  //return false 代表不可以做 Update 的 render
  shouldComponentUpdate(nextProps,nextState){
    if(nextState.name !== this.state.name)
    {
      return true;
    }
  }

  //getSnapshotBeforeUpdate 在 render 與 render產生DOM 之間把目前狀態做儲存可以用
  getSnapshotBeforeUpdate(prevProps,prevState){
    return null;
  }
  //componentDidUpdate 更新後可以修改 render 的 DOM 內容樣式 // fetch DOM style js...
  componentDidUpdate(prevProps,prevState,snapshot){
    if(this.context.name === "王大明" && this.state.first === true){
      document.getElementById('text-area').style.backgroundColor = "gray";
    }
    else {
      document.getElementById('text-area').style.backgroundColor = "green";
    }
  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI.
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    // You can also log the error to an error reporting service
    console.log(error, errorInfo);
  }

  //render 渲染的return (DOM);
  render(){
    if (this.state.hasError) {
      // You can render any custom fallback UI
      return <h1>系統出錯!!</h1>;
    }
    if(this.state.first === true){
      return(
        <>
          <header>
            header
          </header>
            <div id="text-area">
              <Boy></Boy>
            </div>
              <button onClick={this.changeName}>
              {this.context.name}
            </button>      
          <footer>
            footer
          </footer>
        </>
      );
    }
    else {
      return(
        <>
          <header>
            header
          </header>
            <div id="text-area">
            </div>
            <button onClick={this.changeName}>
            {this.context.name}
            </button>      
          <footer>
            footer
          </footer>
        </>
      );
    }
  }
}
export default App;
