import React from 'react';
import { NavbarProvider } from './NavbarContext';
import Navbar from './Navbar';

//Navbar
const navlink = {
    Target1: {
        link: "/Login",
        item: "登入"
    },
    Target2: {
        link: "/Register",
        item: "註冊"
    }
};

//Navbar
const navlink2 = {
    Target1: {
        link: "/",
        item: "系級課程"
    },
    Target2: {
        link: "/Activity",
        item: "通識"
    },
    Target3: {
        link: "/Product",
        item: "體育/全校課程"
    },
    Target4: {
        link: "/Teach",
        item: "教育學程"
    },
};

const Header = () => {
    let token = localStorage.getItem("token");
    if (token) {
        var url = 'http://localhost/dashboard/practice_project/php_server/auto_login.php';
        let obj = {
            key: token,
        }
        var ret;
        fetch(url, {
            method: 'POST',
            body: JSON.stringify(obj)
        })
            .then(function (response) {
                return response.text();
            })
            .then(function (body) {
                ret = body; 
            });
            console.log(ret);
            if(window.ret.code)
            {
                return (
                    <>
                        <div id="header">
                            <NavbarProvider value={navlink2}>
                                <Navbar></Navbar>
                            </NavbarProvider>
                        </div>
                    </>
                );
            }
            else
            {
                return (
                    <>
                        <div id="header">
                            <NavbarProvider value={navlink}>
                                <Navbar></Navbar>
                            </NavbarProvider>
                        </div>
                    </>
                );
            }
            
    }
    else
    {
        return (
            <>
                <div id="header">
                    <NavbarProvider value={navlink}>
                        <Navbar></Navbar>
                    </NavbarProvider>
                </div>
            </>
        );
    }
}

export default Header;
