import React from "react";
import fetch from 'node-fetch';



//function component
const Progress = () => {
    //useState(傳入參數) 解構給 name 變數 和 changeName 函式 
    return (
        <React.StrictMode>
            <header>
                header
            </header>
            <div>
                <button onClick={() => {
                    console.log(document.getElementById("sel-year").value);
                    console.log(document.getElementById("sel-career").value);
                    console.log(document.getElementById("sel-career").value);
                    console.log(document.getElementById("sel-dept").value);
                    console.log(document.getElementById("sel-level").value);
                    console.log(document.getElementById("sel-lang").value);
                    console.log(document.getElementById("sel-text").value);
                    console.log(document.getElementById("sel-teach").value);
                    console.log(document.getElementById("sel-week").value);
                    console.log(document.getElementById("sel-mtg").value);
                    console.log(document.getElementById("sel-emi").value);
                    fetch('http://localhost:3000/', {
                        method: 'POST',
                        body: "v_year=&v_career=U&v_excel=0&v_dept=U64F&v_level=&v_lang=&v_text=&v_teach=&v_week=&v_mtg=&v_emi=",
                        headers: {
                            'Accept': 'text/document',
                            'Content-Type': 'text/plain'
                        }
                    }).then(res => res.text())
                        .then(ress => {
                            var parser = new DOMParser();
                            var doc = parser.parseFromString(ress, 'text/html');
                            console.log(doc);
                            let myNodeList = doc.getElementsByName("mytable");
                            for (let i = 0; i < myNodeList.length; i++) {
                                document.getElementById("content").appendChild(myNodeList[i]);
                            }
                        })
                }}>按我</button>

            </div>
            <footer>
                footer
            </footer>
        </React.StrictMode>
    );
    //fetch(url).then(參數 => 回傳值).then(參數 => 函式內容).catch(err => 回報錯誤)
}

export default Progress;
