import React, { useEffect,useRef } from "react";
import Boy from "./Boy";

const Product = (props) => {
    //useEffect 的用法有四種 
    //
    //useEffect(() => {componentDidMount + componentDidUpdate內容},[props參數])
    //useEffect(() => {componentDidMount return (ComponentWillUnmount內容);},[])
    //useEffect(() => {componentDidMount + componentDidUpdate內容 return (ComponentWillUnmount內容);},[props參數])
    //useEffect(() => {componentDidMount + componentDidUpdate內容 return (ComponentWillUnmount內容);})
    const mounted = useRef();
    useEffect(() => {
        if(mounted.current === false)
        {
            if (props.first) {
                document.getElementById("content").style.fontSize = "30px";
            }
            else {
                document.getElementById("content").style.fontSize = "24px";
            } 
        }
        else
        {
            if (props.first) {
                document.getElementById("content").style.fontSize = "60px";
            }
            else {
                document.getElementById("content").style.fontSize = "24px";
            } 
        }
        
    }, [props.first]);
    if (props.first) {
        return (
            <>
                <div id="content"> 
                    <Boy />
                </div>
            </>

        );
    }
    else {
        return (
            <>
                <div id="content">
                    <Boy />
                </div>
            </>

        );
    }
}

export default Product;
