import React, {useState} from "react";
import { Value } from "sass";

const Login = () => {
    const [Data, setData] = useState({
        username: "",
        password: ""
    });
    return (
        <>
            <div id="login-form">
                帳號 : <input type="text" name="username" id="username" onChange={setData(Value,Data.password)}/><br/>
                密碼 : <input type="text" name="password" id="password" onChange={setData(Data.username,Value)}/>   
                <button onClick={() => {
                    var url = 'http://localhost/dashboard/practice_project/php_server/login.php';
                    let obj = {
                        email: "t7878780@gamil.com",
                        name: "asddzxcc",
                        password: "asddzzxccca"
                    }
                    fetch(url,{
                        method: 'POST',
                        body: JSON.stringify(obj)
                    }
                    )
                    .then(function (response) {
                      return response.text();
                    })
                    .then(function (body) {
                      console.log(body);
                    });
                }}>登入</button>
            </div>
        </>
    );
}

export default Login;
